let planetval;const fetchInfo=()=>{axios.get("https://skoodath.github.io/solar-system/content/planetinfo.json").then(e=>{planetval=e.data})};fetchInfo();const planets=document.getElementsByClassName("planet_name"),closeInfo=document.querySelector(".planet_details_close");for(let n=0;n<planets.length;n++)planets[n].addEventListener("click",()=>{const e=document.querySelector(".planet_details_wrapper");e.classList.contains("planet_details_hide")&&e.classList.replace("planet_details_hide","planet_details_show");var t=planetval.filter(e=>e.name===planets[n].innerHTML);updatePlanetData(t)});const updatePlanetData=e=>{const t=document.querySelector(".planet_card_title"),n=document.querySelector(".planet_details_img"),a=document.querySelector(".planet_details_circum"),l=document.querySelector(".planet_details_orb_period"),s=document.querySelector(".planet_details_orb_speed"),r=document.querySelector(".planet_details_moons"),o=document.querySelector(".planet_details_distancefromsun"),c=document.querySelector(".planet_details_distancefromearth"),i=document.querySelector(".planet_details_surfacetemp");t.innerHTML=e[0].name,n.setAttribute("src",e[0].image),n.setAttribute("alt",e[0].name),a.innerHTML=`Circumference: ${e[0].circumference}`,l.innerHTML=`Orbit Period: ${e[0].orbit_period}`,s.innerHTML=`Orbit Speed: ${e[0].orbit_speed}`,r.innerHTML=`No.of Moons: ${e[0].moons}`,o.innerHTML=`Distance from Sun: ${e[0].distancetosun}`,c.innerHTML=`Distance from Earth: ${e[0].distancetoearth}`,i.innerHTML=`Surface Temperature: ${e[0].avg_temp}`};closeInfo.addEventListener("click",()=>{const e=document.querySelector(".planet_details_wrapper");e.classList.contains("planet_details_show")&&e.classList.replace("planet_details_show","planet_details_hide")});